import React, { Component } from "react";
import "./App.css";
import SearchLocation from "./component/SearchLocation";
class App extends Component {
  state = {
    lat: null,
    log: null, 
    cityName:"",
    temperature:"", 
    weather:"", 
    icon:"",
    wind_speed:"", 
    Humidity:""
  };
  componentDidMount() {

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        console.log(position, "position");
        let latitude = position.coords.latitude;
        let longitude = position.coords.longitude;
        this.setState=({lat:latitude,log:longitude}) 

      });

    this.getWeather();
    }


  }   
  getWeather = async(e)=>{
   const city = e.cityName;
   const api_call = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&apikey=cf9fb4df751879d3f30929a3dd9050e4`); 

   const response = await api_call.json(); 
   console.log(response); 
   if(city!==""){
     this.setState({
       temperature: response.main.temp

     })

   }
   else{
     console.log("iffjjf")
   }
}    

  render() {
    return (
      <div className="App">
        <h1> WHAT'S THE WEATHER? </h1>
        <SearchLocation city={this.state.cityName} gitWeather={this.getWeather}/>
      </div>
    );
  }

}

export default App;
