import React, { Component } from 'react'
class SearchLocation extends Component {
    state = {
        city: ''
    }
    inputChange = (e) => {
        const cityName = e.target.value;
        this.setState({ city: e.target.value });
        console.log(cityName);
    }
    searchLocation = (e) => {
        e.preventDefault();
        console.log('submit', this.state);
        document.getElementById('SearchLocation').value = '';
        this.props.onSubmit(this.state.text);

    }


    render() {
        const { city, gitWeather } = this.props;
        return <div className='LocationContainer'>
            <form onSubmit={(e) => this.searchLocation(e)}>
                <input className='InputText' id='SearchLocation'placeholder="Enter your City to find out ..."
                 onChange={(e) => this.inputChange(e)} type="text"></input>
                <button className='SearchButton' type="submit" onClick={() => gitWeather(city)}> Get Weather </button>
            </form>

        </div>
    }
}
export default SearchLocation;
